let mainFunc=()=>{const t=document.querySelector("body");function e(){dx=clientX-cx,dy=clientY-cy,tiltX=dy/cy,tiltY=dx/cx,radius=Math.sqrt(Math.pow(tiltX,2)+Math.pow(tiltY,2)),degree=20*radius,gsap.to(".content",1,{transform:`rotate3d( ${-tiltX}, ${tiltY}, 0, ${degree}deg)`})}cx=window.innerWidth/2,cy=window.innerHeight/2,t.addEventListener("mousemove",(t=>{clientX=t.pageX,clientY=t.pageY,request=requestAnimationFrame(e)}))};document.addEventListener("DOMContentLoaded",(function(t){mainFunc(),document.addEventListener("resize",mainFunc)}));